INSERT INTO `vakants` (`id`, `id_factory`, `text`, `dolzhnostt`, `zarplata`, `date`) VALUES
	(1, 2, '<p>Отдел управления персоналом  ТОО «Богатырь Комир» требуются: начальник участка по техническому обслуживанию и ремонту горно-транспортного  оборудования (Управление технологического транспорта)</p><p>Резюме принимаются: <br>г. Экибастуз, ул. Б. Момышулы 23 <br>Пост охраны, с 8-00 до 17-00<br> Электронная почта  -  odo@bogatyr.kz</p>', 'начальник участка по техническому обслуживанию и ремонту горно-транспортного  оборудования (Управление технологического транспорта)', '200 000KZT', '2022-03-17 14:15:04'),
	(2, 2, '<p>Отдел управления персоналом  ТОО «Богатырь Комир» требуются:мастер по ремонту вагонов;</p><p>Резюме принимаются: <br>г. Экибастуз, ул. Б. Момышулы 23 <br>Пост охраны, с 8-00 до 17-00<br> Электронная почта  -  odo@bogatyr.kz</p>', 'мастер по ремонту вагонов;', '150 000KZT', '2022-03-17 14:15:42'),
	(3, 2, '<p>Отдел управления персоналом  ТОО «Богатырь Комир» требуются: слесарь;</p><p>Резюме принимаются: <br>г. Экибастуз, ул. Б. Момышулы 23 <br>Пост охраны, с 8-00 до 17-00<br> Электронная почта  -  odo@bogatyr.kz</p>', 'слесарь', '260 000KZT', '2022-03-17 14:20:38'),
	(4, 2, '<p>Отдел управления персоналом  ТОО «Богатырь Комир» требуются: токарь;</p><p>Резюме принимаются: <br>г. Экибастуз, ул. Б. Момышулы 23 <br>Пост охраны, с 8-00 до 17-00<br> Электронная почта  -  odo@bogatyr.kz</p>\r\n', 'токарь', '200 000KZT', '2022-03-17 14:21:37'),
	(5, 1, '<p>АО "КазМунайГаз - переработка и маркетинг" объявляет открытый конкурс на замещение вакантных должностей.</p> <p>Отдел управления персоналом АО "КазМунайГаз» требуются: начальник участка по техническому обслуживанию и ремонту горно-транспортного оборудования (Управление технологического транспорта);</p> <h3>Этапы отбора кандидатов</h3> <p>1 этап<br>Прием резюме кандидатов на сайте www.kmgrm.kz/vacancies</p> <p>2 этап<br>Отбор резюме кандидатов, соответствующих квалификационным требованиям. Комиссия имеет право запросить документы, подтверждающие достоверность сведений, указанных в резюме.</p> <p>3 этап <br>Прохождение кандидатами тестирования, соответствующими квалификационным требованиям. Место проведения: г. Астана.</p> <p>Тесты: <br> <ul> <li>- Профессиональные знания</li> <li>- Анализ вербальной информации</li> <li>- Анализ числовой информации</li> <li>- Знание языков</li> </ul> </p> <p>4 этап<br>Прохождение кандидатами собеседования с Конкурсной комиссией. На собеседование приглашаются кандидаты демонстрирующие результаты тестирования соответствующие и выше пороговых значений для вакантной должности. Место проведения: г. Астана.</p> <p>5 этап Оформление документов на прием кандидатов, успешно прошедших конкурсные процедуры. Кандидаты несут ответственность за подлинность достоверной информации в резюме.</p> ', 'начальник участка по техническому обслуживанию и ремонту горно-транспортного  оборудования (Управление технологического транспорта)', '200 000KZT', '2022-03-17 14:15:04'),
	(6, 1, '<p>АО "КазМунайГаз - переработка и маркетинг" объявляет открытый конкурс на замещение вакантных должностей.</p> <p>Отдел управления персоналом АО "КазМунайГаз» требуются: мастер по ремонту вагонов;</p> <h3>Этапы отбора кандидатов</h3> <p>1 этап<br>Прием резюме кандидатов на сайте www.kmgrm.kz/vacancies</p> <p>2 этап<br>Отбор резюме кандидатов, соответствующих квалификационным требованиям. Комиссия имеет право запросить документы, подтверждающие достоверность сведений, указанных в резюме.</p> <p>3 этап <br>Прохождение кандидатами тестирования, соответствующими квалификационным требованиям. Место проведения: г. Астана.</p> <p>Тесты: <br> <ul> <li>- Профессиональные знания</li> <li>- Анализ вербальной информации</li> <li>- Анализ числовой информации</li> <li>- Знание языков</li> </ul> </p> <p>4 этап<br>Прохождение кандидатами собеседования с Конкурсной комиссией. На собеседование приглашаются кандидаты демонстрирующие результаты тестирования соответствующие и выше пороговых значений для вакантной должности. Место проведения: г. Астана.</p> <p>5 этап Оформление документов на прием кандидатов, успешно прошедших конкурсные процедуры. Кандидаты несут ответственность за подлинность достоверной информации в резюме.</p> ', 'мастер по ремонту вагонов;', '150 000KZT', '2022-03-17 14:15:42'),
	(7, 1, '<p>АО "КазМунайГаз - переработка и маркетинг" объявляет открытый конкурс на замещение вакантных должностей.</p> <p>Отдел управления персоналом АО "КазМунайГаз» требуются: слесарь;</p> <h3>Этапы отбора кандидатов</h3> <p>1 этап<br>Прием резюме кандидатов на сайте www.kmgrm.kz/vacancies</p> <p>2 этап<br>Отбор резюме кандидатов, соответствующих квалификационным требованиям. Комиссия имеет право запросить документы, подтверждающие достоверность сведений, указанных в резюме.</p> <p>3 этап <br>Прохождение кандидатами тестирования, соответствующими квалификационным требованиям. Место проведения: г. Астана.</p> <p>Тесты: <br> <ul> <li>- Профессиональные знания</li> <li>- Анализ вербальной информации</li> <li>- Анализ числовой информации</li> <li>- Знание языков</li> </ul> </p> <p>4 этап<br>Прохождение кандидатами собеседования с Конкурсной комиссией. На собеседование приглашаются кандидаты демонстрирующие результаты тестирования соответствующие и выше пороговых значений для вакантной должности. Место проведения: г. Астана.</p> <p>5 этап Оформление документов на прием кандидатов, успешно прошедших конкурсные процедуры. Кандидаты несут ответственность за подлинность достоверной информации в резюме.</p> ', 'слесарь', '260 000KZT', '2022-03-17 14:20:38'),
	(8, 1, '<p>АО "КазМунайГаз - переработка и маркетинг" объявляет открытый конкурс на замещение вакантных должностей.</p> <p>Отдел управления персоналом АО "КазМунайГаз» требуются: токарь;</p> <h3>Этапы отбора кандидатов</h3> <p>1 этап<br>Прием резюме кандидатов на сайте www.kmgrm.kz/vacancies</p> <p>2 этап<br>Отбор резюме кандидатов, соответствующих квалификационным требованиям. Комиссия имеет право запросить документы, подтверждающие достоверность сведений, указанных в резюме.</p> <p>3 этап <br>Прохождение кандидатами тестирования, соответствующими квалификационным требованиям. Место проведения: г. Астана.</p> <p>Тесты: <br> <ul> <li>- Профессиональные знания</li> <li>- Анализ вербальной информации</li> <li>- Анализ числовой информации</li> <li>- Знание языков</li> </ul> </p> <p>4 этап<br>Прохождение кандидатами собеседования с Конкурсной комиссией. На собеседование приглашаются кандидаты демонстрирующие результаты тестирования соответствующие и выше пороговых значений для вакантной должности. Место проведения: г. Астана.</p> <p>5 этап Оформление документов на прием кандидатов, успешно прошедших конкурсные процедуры. Кандидаты несут ответственность за подлинность достоверной информации в резюме.</p> ', 'токарь', '200 000KZT', '2022-03-17 14:21:37');
